export const WhatShouldWeBuildScreen: React.FC = () => {
	const frame = useCurrentFrame();
	const {fps, width, height, durationInFrames} = useVideoConfig();

	// Multi-phase choreography:
	// 1) Intro: elements slide/blur in with stagger
	// 2) Settle: slight breathing on the card border glow
	// 3) Outro: fade + drift down
	const outroStart = Math.max(0, durationInFrames - Math.round(fps * 0.9));
	const outroT = clamp((frame - outroStart) / (durationInFrames - outroStart), 0, 1);
	const outroEase = easeOutCubic(outroT);

	const globalOpacity = interpolate(outroEase, [0, 1], [1, 0]);
	const globalY = interpolate(outroEase, [0, 1], [0, 18]);

	const cardW = Math.min(1352, width - 140);
	const cardH = Math.min(260, Math.max(230, Math.round(height * 0.26)));

	const borderPulse = useMemo(() => {
		// A soft “alive” pulse after intro has completed.
		const introDone = spring({frame, fps, config: {damping: 20, stiffness: 90}}); // ~first second
		const alive = Math.sin(frame / 20) * 0.5 + 0.5;
		return interpolate(introDone, [0, 1], [0, 1]) * alive;
	}, [frame, fps]);

	// Caret: use interpolate for a smoother blink (not boolean).
	const caret = interpolate(Math.sin((frame / fps) * Math.PI * 2 * 2.2), [-1, 1], [0.2, 1]);

	// Icon micro-bounce (staggered), subtle.
	const iconBob = (phase: number) =>
		Math.sin((frame + phase) / 14) * 1.6 * interpolate(borderPulse, [0, 1], [0, 1]);

	return (
		<AbsoluteFill
			style={{
				backgroundColor: "transparent",
				justifyContent: "center",
				alignItems: "center",
				fontFamily: FONT,
			}}
		>
			<GridBg frame={frame} />

			<div
				style={{
					transform: `translate3d(0, ${globalY}px, 0)`,
					opacity: globalOpacity,
					willChange: "transform, opacity",
				}}
			>
				{/* Main input card */}
				<AnimatedIn frame={frame} fps={fps} delay={6} from={{y: 26, scale: 0.985, blur: 14}}>
					<div
						style={{
							width: cardW,
							height: cardH,
							borderRadius: 26,
							background: "rgba(22,28,66,0.62)",
							boxShadow: "0 22px 55px rgba(0,0,0,0.35)",
							position: "relative",
							overflow: "hidden",
						}}
					>
						{/* Gradient border with pulse */}
						<div
							style={{
								position: "absolute",
								inset: 0,
								borderRadius: 26,
								border: "8px solid transparent",
								background:
									"linear-gradient(rgba(22,28,66,0.62), rgba(22,28,66,0.62)) padding-box, linear-gradient(90deg, #7a4dff 0%, #c07ab6 45%, #d7b060 100%) border-box",
								pointerEvents: "none",
								opacity: 0.9 + borderPulse * 0.12,
								filter: `saturate(${1 + borderPulse * 0.08})`,
							}}
						/>

						{/* Inner sheen */}
						<div
							style={{
								position: "absolute",
								inset: 0,
								borderRadius: 26,
								background:
									"radial-gradient(900px 260px at 20% 10%, rgba(255,255,255,0.12) 0%, rgba(255,255,255,0.0) 62%)",
								opacity: 0.35,
								pointerEvents: "none",
							}}
						/>

						{/* Headline with dedicated motion */}
						<div style={{position: "absolute", left: 34, top: 40}}>
							<RevealText
								frame={frame}
								fps={fps}
								delay={12}
								text="Ask Agent Swarm to create a"
								fontSize={30}
								color="rgba(210,220,255,0.35)"
								weight={500}
							/>
						</div>

						{/* Input pill (staggered after headline) */}
						<div style={{position: "absolute", left: 34, top: 134}}>
							<AnimatedIn frame={frame} fps={fps} delay={20} from={{y: 16, scale: 0.98, blur: 10}}>
								<div
									style={{
										height: 56,
										borderRadius: 999,
										paddingLeft: 16,
										paddingRight: 22,
										display: "flex",
